import { useState, useEffect } from 'react';

const Upload = () => {

    const [uploads, setUploads] = useState([]);
    const [name, setName] =useState([]);
    const [files, setFiles] = useState([]);

    useEffect(() => {

    const uploadElement = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("name", name.value);
    for(let i =0; i < files.files.length; i++) {
            formData.append("files", files.files[i]);
    }
    fetch("http://localhost:5002/upload_files", {
        method: 'POST',
        body: formData
        // We avoid specifying headers content-type by ourselve as we let browser set boundary and content-type
    })
        .then((res) => console.log(res))
        .catch((err) => ("Error occured", err));
}


        fetch('http://localhost:5000/products')
            .then((response) => response.json())
            .then((data) => setUploads(data));
    }, []);

    return (
        <>
        <div class="container">
            <h1>File Upload</h1>
            <form id='form'>
                <div class="input-group">
                    <label for='name'>Your name</label>
                    <input name='name' id='name' placeholder="Enter your name" />
                </div>
                <div class="input-group">
                    <label for='files'>Select files</label>
                    <input id='files' type="file" multiple />
                </div>
                <button class="submit-btn" type='submit' onClick={uploadElement}>Upload</button>
            </form>
        </div>
        </>
    );
};

export default Upload;
