import { useState, useEffect } from 'react';
import '../styles/upload.css';

export const UploadForm = () => {
    const [flashMessage, setFlashMessage] = useState('');
    const [showUpload, setShowUpload] = useState('');
    const [getUpload, setGetUpload] = useState(false);

    const getMyUpload = (e) => {  
        if(!getUpload) {  
        setGetUpload(true)
        e.target.textContent = 'Stop downloading';
      }

      if(getUpload) {  
        setGetUpload(false)
        e.target.textContent = 'Download my files';
      } 
    }

    useEffect(() => {
      const fetchCategoriesUpload = async () => {
          try {
              const response = await fetch("http://localhost:5000/download_files");
              if(response.ok) {
              const data = await response.json();
              console.log('download successful');
              setShowUpload(data);
            } else  {
              const data = await response.text();
              console.log(data);
             }
          } catch (error) {
              console.error("Error fetching files:", error);
          }
         
      };

      if(getUpload) {
       fetchCategoriesUpload()
      }
    });
  
      const sendFile = async (e) => {
          e.preventDefault();
          const form = document.getElementById('form');
          const downloadButton = document.getElementById('download-btn');
          downloadButton.style.display = ''
          const formData = new FormData(form);
          
          try {
            const response = await fetch('http://localhost:5000/upload_files', {
              method: 'POST',
              body: formData,
            });
            
            if (response.ok) {
              // Handle successful upload
              console.log('Upload successful!');
              const data = await response.json();
                console.log(data)

                data.map((item) => {   
                  console.log(item.filename);
                  setFlashMessage('Vous avez bien créer votre fichier image nommé: '+item.filename);
                  setTimeout(() => {
                    setFlashMessage('');
                  }, 3000);
                  return item.filename;
                });

             
            
            } else if (response.status === 409) {
              // Handle image name already taken error
              const data = await response.json();
              console.log(data.error);
              setFlashMessage(data.error);
            } else if (response.status === 400) {
              // Handle upload error
              console.error('Upload failed!');
              setFlashMessage('Il y a eu un problème avec la création de votre image');
              setTimeout(() => {
                setFlashMessage('');
              }, 3000);
            }
          } catch (error) {
            console.error('Upload failed:', error);
            setFlashMessage('Il y a eu une erreur dans la requête');
            setTimeout(() => {
              setFlashMessage('');
            }, 3000);
          }
        };

        console.log(showUpload);
  
      return (
        <>
          <div className="container">
           
          {flashMessage && <div className="output-message">{flashMessage}</div>}
              <div className="container_inner">
                  <h1>Envoyez vos photos de produits</h1>
                  <form id='form'>
                      <div className="input-group">
                          <label htmlFor='name'>Catégorie</label>
                          <input name='name' id='name' placeholder="Nom de la catégorie d'image" />
                      </div>
                      <div className="input-group">
                          <label htmlFor='files'>Select files</label>
                          <input name='files' id='files' type="file" multiple />
                      </div>
                      <button className="submit-btn" type='submit' onClick={sendFile}>Upload</button>
                      <button className="submit-btn btn-download" type='button' id='download-btn' onClick={getMyUpload}>Download my files</button>
                  </form>
              </div>  
              <div>
          {/* {showUpload.map((upload, index) => (
            <div key={index}>
              <h2>{upload}</h2>
              <p>{upload}</p>
            </div>
          ))} */}
        </div>        
          </div>
        </>
      );
  };
