import { useContext, Fragment, useEffect, useS } from 'react';
import { CategoriesContext } from '../context/categories.context';
import { CategoryPreview } from '../components/category-preview.component';

export const CategoriesPreview = () => {
    const { categoriesData } = useContext(CategoriesContext)
    const [products, setProducts] = useState(categoriesData);
   
    console.log(categoriesData.category);

    useEffect( () => {
        setProducts(categoriesData);
    }, [category, categoriesData])

    return (
        <Fragment>
            {categoriesData.map((title) =>{
                const products = categoriesData;
                return <CategoryPreview key={title} title={title} products={products} />
            })}
        </Fragment>
    );
};
