import '../styles/checkout-item.scss';
import {useContext} from "react";
import {CartContext} from "../context/cart.context";

export const CheckoutItem = ({cartItem}) => {

//     (3) [{…}, {…}, {…}]
// 0
// : {id: 11, category_id: 2, category: 'legumes', product_name: 'Poivron', product_image_url: 'legumes_poivron.jpg', …}
// 1
// : {id: 10, category_id: 2, category: 'legumes', product_name: 'chou fleur', product_image_url: 'legumes_chou_fleur.jpg', …}
// 2
// : {id: 12, category_id: 2, category: 'legumes', product_name: 'Tomate', product_image_url: 'legumes_tomate.jpg', …}

    const { product_name, product_image_url, product_price, quantity } = cartItem;
    const {  removeItemFromCart } = useContext(CartContext);
    const removeProductFromCart = () => removeItemFromCart(cartItem);

    if(category && product_name){
        let product_nameUrl = product_name.toLowerCase().split(' ').filter(word => word !== '').join('')
        imageUrl = `http://localhost:5000/uploads/${category}_${product_nameUrl}.jpeg`;
      }

    return(
        <div className='checkout-item-container'>
            <div className='image-container'>
                <img src={product_image_url} alt={`${product_name}`} />
            </div>
            <span className='name'> {product_name} </span>
            <span className='quantity'> {quantity}</span>
            <span className='price'>{product_price}€</span>
            <div onClick={removeProductFromCart} className='remove-button'>&#10005;</div>
        </div>
    )

}

export default CheckoutItem;
