import React, { useState } from 'react';
import '../index.scss';

const Connexion = () => {
  const [toggle, setToggle] = useState(true);
  const [flashMessage, setFlashMessage] = useState('');

  const handleToggle = () => {
    setToggle(!toggle);
  };

  const sendFile = async (e) => {
    e.preventDefault();
    const form = document.getElementById('form');
    console.log(form);
    const formData = new FormData(form);
    
    // Check if the required fields are empty
    const requiredInputs = form.querySelectorAll('[required]');
    let isEmpty = false;
    requiredInputs.forEach((input) => {
      if (!input.value) {
        isEmpty = true;
        input.classList.add('error');
      } else {
        input.classList.remove('error');
      }
    });

    if (isEmpty) {
      setFlashMessage('Veuillez remplir tous les champs obligatoires');
      setTimeout(() => {
        setFlashMessage('');
      }, 3000);
      return;
    }

    const categoryName = formData.get('category_name');
    const categoryNameId = formData.get('category_name_id');
    const imageName = formData.get('image_name');
    const productImageUrl = categoryName+'_'+imageName+'.jpeg';
    const productPrice = formData.get('purchasing_product_price') * 1.2;

    try {
      const response = await fetch('http://localhost:5000/upload_files', {
        method: 'POST',
        body: formData,
      });
      
      if (response.ok) {
        // Handle successful upload
        console.log('Upload successful!');
        const data = await response.json();
        console.log(data)

          data.map((item) => {   
            console.log(item.filename);
            setFlashMessage('Vous avez bien créer votre fichier image nommé: '+item.filename);
            setTimeout(() => {
              setFlashMessage('');
            }, 3000);
            return item.filename;
          });

      } else if (response.status === 409) {
        const data = await response.json();
        console.log(data.error);
        setFlashMessage(data.error);
      } else if (response.status === 403) {
        console.error('number max');
        setFlashMessage('Nombre maximum de fichiers atteint');
      } else if (response.status === 400) {
        console.error('Upload failed!');
        setFlashMessage('Il y a eu un problème avec la création de votre image');
        setTimeout(() => {
          setFlashMessage('');
        }, 3000);
      }
    } catch (error) {
      console.error('Upload failed:', error);
      setFlashMessage('Il y a eu une erreur dans la requête');
      setTimeout(() => {
        setFlashMessage('');
      }, 3000);
    }
  };

  return (
    <main className="page-connexion">
      <div className="outer-connexion">
        <div className="inner-connexion">
          {toggle ? (
            <div className="container-inscription">
              <form className="form-container" action="" method="post">
                <div className="form-elem">
                  <input type="text" name="username" id="username" placeholder="Votre prénom" />
                </div>
                <div className="form-elem">
                  <input type="text" name="lastname" id="lastname" placeholder="Votre nom" />
                </div>
                <div className="form-elem">
                  <input type="text" name="email" id="email" placeholder="Votre email" />
                </div>
                <div className="form-elem">
                  <input type="password" name="password" id="password" placeholder="mot de passe" />
                </div>
                <div className="form-elem">
                  <input type="number" name="phone" id="phone" placeholder="téléphone" />
                </div>
                <div className="form-elem">
                  <button className="btn-1" type="submit">Créer son compte</button>
                </div>
                <div className="form-elem">
                  <button type="button" className="btn-2" onClick={handleToggle}>Se connecter</button>
                </div>
              </form>
            </div>
          ) : (
            <div className="container-connexion">
              <div className="img-container">
                <img src="http://localhost:5000/assets/boiler-logo-2.svg" alt="logo" />
              </div>
              <div className="info-container">
                <div></div>
              </div>
              <div className="form-container">
                <form action="auth/logged" method="post">
                  <div className="form-elem">
                    <input type="text" name="username" id="email" placeholder="username" value="" />
                  </div>
                  <div className="form-elem">
                    <input type="password" name="password" id="password" placeholder="Mot de passe" value="" />
                  </div>
                  <div className="form-elem">
                    <button className="btn-1">Se connecter</button>
                  </div>
                  {/* <div className="form-elem">
                    <button type="button" className="btn-2">Créer un compte</button>
                  </div> */}
                </form>
              </div>
            </div>
          )}
        </div>
      </div>
    </main>
  );
};

export default Connexion;
