import React, { useEffect, useState } from 'react';
import '../styles/fileList.css';

const FileList = () => {
  const [fileList, setFileList] = useState([]);

  useEffect(() => {
    const fetchFiles = async () => {
      try {
        const response = await fetch('http://localhost:5000/images_names');
        if (response.ok) {
          const data = await response.json();
          setFileList(data.images);
        } else {
          console.error('Failed to fetch files:', response.status);
        }
      } catch (error) {
        console.error('Error fetching files:', error);
      }
    };

    fetchFiles();
  }, []);

  return (
    <div>
      <h2>File List:</h2>


      <section class="table-container">

    <div id="table">

    <div id="table-header">
        <div id="header-datas table-row">
            <div class="header-cell">Fichier</div>
            <div class="header-cell">Cat√©gorie</div>
            <div class="header-cell">Action</div>
        </div>
    </div>

    <div class="body-row" id="clients-list">
    {fileList.filter((file, index) => index < 10).map((file, index) => (
        <>
          <div key={index}>{file}</div>
          <div></div>
          <div></div>
        </>
        ))}
    </div>
</div>

</section>

      <ul>
      {fileList.filter((file, index) => index < 10).map((file, index) => (
        <li key={index}>{file}</li>
        ))}
      </ul>
    </div>
  );
};

export default FileList;
