import { useContext, useState, useEffect, Fragment } from 'react';
import { useParams } from 'react-router-dom';
import { CategoriesContext } from '../context/categories.context';
import { ProductCard } from '../components/product-card';
import '../styles/category.scss';

export const Category = () => {
    let { category } = useParams();
    const { categoriesData } = useContext(CategoriesContext);
    const [products, setProducts] = useState(categoriesData);
    const [imageList, setImageList] = useState([]);
    const noProductsMessage = <p className="category-text">Il n'y a pas de produits Ã  vendre pour le moment.</p>;

    useEffect( () => {
        setProducts(categoriesData);
    }, [category, categoriesData])
  console.log(products);
    function removeAccents(str) {
        return str.normalize("NFD").replace(/[\u0300-\u036f]/g, "");
    }

    category = removeAccents(category.toLowerCase());

    useEffect(() => {
        const fetchImages = async () => {
          try {
            const response = await fetch('http://localhost:5000/images_names');
            if (response.ok) {
              const data = await response.json();
              setImageList(data.images);
            } else {
              console.error('Error fetching images:', response.status);
            }
          } catch (error) {
            console.error('Error fetching images:', error);
          }
        };
    
        fetchImages();
      }, []);
  
      let product_nameUrl = removeAccent(product_name).toLowerCase().split(' ').filter(word => word !== '').join('')
      let imageUrl = `http://localhost:5000/uploads/${category}_${product_nameUrl}.jpeg`

    return(
        <Fragment>
        <h2 className='category-title'>{category.toUpperCase()}</h2>
            <div className='category-container product-container'>
                {!products || !products.length ? (
                    noProductsMessage
                ) : (
                    products.filter((product) => product.category === category.toLowerCase()).map((product) => (
                        <ProductCard key={product.id} product={product} category={category} image_list={category} />
                    ))
                )}
            </div>
        </Fragment>
    )
}
